mapper.select:
Select `COMMUNITY_SEQ`, `MINOR_CD`, `COMMUNITY_USER_ID`, `COMMUNITY_TITLE`, `COMMUNITY_CONTENT`, `COMMUNITY_IMG1`,`COMMUNITY_IMG2`,`COMMUNITY_IMG3`,`COMMUNITY_IMG4`,`COMMUNITY_IMG5`, `COMMUNITY_REVISE_DATETIME`, `COMMUNITY_LOOKUPCNT`, `COMMUNITY_DELETE_CH`, substring(`COMMUNITY_WRITE_DATETIME`,1,19) as COMMUNITY_WRITE_DATETIME, `COMMUNITY_REVISE_DATETIME`, `COMMUNITY_LOOKUPCNT` From department_community
minor_cd='DEB001' AND 1=1 minor_cd='DEB001' AND community_title like CONCAT('%', #{searchValue} , '%') minor_cd='DEB001' AND community_user_id like CONCAT('%', #{searchValue} , '%')
order by community_write_datetime desc limit #{limitNo},#{pageSize}
