mapper.select:
select a.user_num,a.qna_seq, qna_title, qna_content,qna_state, qna_type, substring(`qna_write_datetime`,1,19) as qna_write_datetime, qnasr_content, substring(`qnasr_write_datetime`,1,19) as qnasr_write_datetime, substring(`qnasr_revise_datetime`,1,19) as qnasr_revise_datetime from qna a left outer join qna_ansr b ON a.qna_seq = b.qna_seq
AND 1=1 AND qna_title like CONCAT('%', #{searchValue} , '%') AND qna_content like CONCAT('%', #{searchValue} , '%')
order by a.qna_seq limit #{limitNo},#{pageSize}
